import React, { useState } from "react";

function CreateArea(props) 
{
  const [text , setText] = useState({});
  function handleChange(event){
    const value= event.target.value ;
    const name = event.target.name;
    setText(prevValue=>{
      return {...prevValue, [ name] : value}
    })   
    console.log(text);
  }
  console.log(props);
  return (
    <div>
      <form>
        <input  onChange={handleChange} name="title" placeholder="Title" value={text.title} />
        <textarea onChange={handleChange} name="content" value={text.content} placeholder="Take a note..." rows="3"  />
        <button type="button" onClick={()=>{props.onAdd(text);
        }}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
